mapper.select:
SELECT houseId, ADDRESS, WHOLEAREA, BATHROOMS, ROOMS, HOUSENAME
FROM HOUSE_TB
WHERE MEMBERID = #{memberId}

mapper.update:
UPDATE HOUSE_TB
SET ADDRESS = #{address} , WHOLEAREA = #{wholeArea} , BATHROOMS = #{bathrooms} ,
ROOMS= #{rooms} , HouseName = #{houseName}
WHERE HOUSEID = #{houseId}
