mapper.select:
SELECT questionId, writerId, shareHouseId, questionDate, phoneNumber, schedule, questionContent
FROM question_tb
WHERE writerId = #{writerId}

SELECT questionId, writerId, shareHouseId, questionDate, phoneNumber, schedule, questionContent
FROM question_tb
WHERE shareHouseId = #{shareHouseId}

SELECT questionId, writerId, shareHouseId, questionDate, phoneNumber, schedule, questionContent
FROM question_tb
WHERE questionId = #{questionId}

mapper.update:
UPDATE question_tb
SET questionDate = #{questionDate}, phoneNumber = #{phoneNumber}, schedule = #{schedule}, questionContent = #{questionContent}
WHERE questionId = #{questionId}
